// filepath: /coffee-sales-analysis/src/components/CafeDashboard.tsx
import React, { useState, useEffect } from 'react';
import { 
  LineChart, Line, BarChart, Bar, PieChart, Pie, 
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, 
  Cell, RadarChart, Radar, PolarGrid, PolarAngleAxis, PolarRadiusAxis,
  AreaChart, Area, ComposedChart, LabelList, Scatter
} from 'recharts';
import { Coffee, Users, DollarSign, ShoppingCart, Clock, TrendingUp, Activity } from 'lucide-react';

const CafeDashboard = () => {
  interface DashboardData {
    totalSales: number;
    customerCount: number;
    avgOrderValue: number;
    totalItems: number;
    salesGrowth: number;
    customerSatisfaction: number;
    hourlyData: { hour: number; sales: number; orders: number; customers: number }[];
    weeklyTrends: { day: string; inStore: number; takeaway: number; delivery: number }[];
    monthlyData: { month: string; sales: number; target: number }[];
    timeBlockData: { name: string; value: number; customers: number }[];
    productPerformance: { category: string; sales: number; customer: number; profit: number; growth: number }[];
  }

  const [data, setData] = useState<DashboardData | null>(null);

  // Enhanced color palette
  const COLORS = {
    primary: '#4F46E5',    // Indigo
    secondary: '#10B981',  // Emerald
    accent: '#F59E0B',     // Amber
    success: '#059669',    // Green
    warning: '#DC2626',    // Red
    info: '#0EA5E9',       // Sky
    purple: '#7C3AED',     // Purple
    pink: '#EC4899'        // Pink
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(value);
  };

  useEffect(() => {
    const fetchData = async () => {
      // Fetch data from the provided source
      const response = await fetch('C:/Users/reddy/Downloads/Coffe Sales Analysis/cafe_sales_data.csv');
      const csvData = await response.text();
      const parsedData = parseCSVData(csvData);

      // Process the parsed data to fit the DashboardData structure
      const processedData = processDashboardData(parsedData);

      setData(processedData);
    };

    fetchData();
  }, []);

  const parseCSVData = (csv) => {
    const lines = csv.split('\n');
    const headers = lines[0].split(',');
    const rows = lines.slice(1).map(line => {
      const values = line.split(',');
      return headers.reduce((obj, header, index) => {
        obj[header.trim()] = values[index].trim();
        return obj;
      }, {});
    });
    return rows;
  };

  const processDashboardData = (rows) => {
    // Process the rows to calculate the required metrics
    const totalSales = rows.reduce((sum, row) => sum + parseFloat(row['Total Price']), 0);
    const customerCount = new Set(rows.map(row => row['Transaction ID'])).size;
    const avgOrderValue = totalSales / customerCount;
    const totalItems = rows.reduce((sum, row) => sum + parseInt(row['Quantity']), 0);
    const salesGrowth = 15.4; // Placeholder value
    const customerSatisfaction = 4.8; // Placeholder value

    // Example data for charts
    const hourlyData = Array.from({length: 24}, (_, i) => ({
      hour: i,
      sales: Math.random() * 5000 + 1000,
      orders: Math.floor(Math.random() * 100 + 20),
      customers: Math.floor(Math.random() * 80 + 15)
    }));

    const weeklyTrends = Array.from({length: 7}, (_, i) => ({
      day: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'][i],
      inStore: Math.random() * 3000 + 1000,
      takeaway: Math.random() * 2000 + 800,
      delivery: Math.random() * 1500 + 500
    }));

    const monthlyData = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'].map(month => ({
      month,
      sales: Math.random() * 20000 + 5000,
      target: Math.random() * 22000 + 5500
    }));

    const timeBlockData = [
      { name: 'Morning â˜€ï¸', value: 35000, customers: 1200 },
      { name: 'Afternoon ðŸŒ¤ï¸', value: 45000, customers: 1500 },
      { name: 'Evening ðŸŒ™', value: 25000, customers: 800 }
    ];

    const productPerformance = [
      { category: 'Coffee â˜•', sales: 85, customer: 90, profit: 80, growth: 75 },
      { category: 'Tea ðŸ«–', sales: 70, customer: 75, profit: 85, growth: 65 },
      { category: 'Pastries ðŸ¥', sales: 90, customer: 85, profit: 90, growth: 80 },
      { category: 'Sandwiches ðŸ¥ª', sales: 75, customer: 80, profit: 70, growth: 85 }
    ];

    return {
      totalSales,
      customerCount,
      avgOrderValue,
      totalItems,
      salesGrowth,
      customerSatisfaction,
      hourlyData,
      weeklyTrends,
      monthlyData,
      timeBlockData,
      productPerformance
    };
  };

  const CustomLabel = ({ x, y, value, stroke }) => (
    <text 
      x={x} 
      y={y} 
      dy={-10} 
      fill={stroke}
      fontSize={12}
      textAnchor="middle"
      fontWeight="600"
    >
      {typeof value === 'number' ? formatCurrency(value) : value}
    </text>
  );

  const MetricCard = ({ icon: Icon, title, value, subValue, color }) => (
    <div className="bg-white p-4 rounded-lg shadow-md border-l-4 hover:shadow-lg transition-shadow duration-200" 
         style={{ borderLeftColor: color }}>
      <div className="flex items-center justify-between">
        <div>
          <p className="text-gray-500 text-sm font-medium">{title}</p>
          <p className="text-2xl font-bold" style={{ color }}>{value}</p>
          {subValue && <p className="text-sm text-gray-600">{subValue}</p>}
        </div>
        <div className="p-2 rounded-full" style={{ backgroundColor: `${color}15` }}>
          <Icon size={24} color={color} />
        </div>
      </div>
    </div>
  );

  if (!data) return <div>Loading...</div>;

  return (
    <div className="p-6 bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Advanced Cafe Analytics Dashboard â˜•</h1>

      {/* Metrics Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <MetricCard
          icon={DollarSign}
          title="Total Sales"
          value={formatCurrency(data.totalSales)}
          subValue={`Growth: +${data.salesGrowth}%`}
          color={COLORS.success}
        />
        <MetricCard
          icon={Users}
          title="Total Customers"
          value={data.customerCount.toLocaleString()}
          subValue={`Satisfaction: â­ ${data.customerSatisfaction}/5`}
          color={COLORS.primary}
        />
        <MetricCard
          icon={ShoppingCart}
          title="Average Order Value"
          value={formatCurrency(data.avgOrderValue)}
          subValue={null}
          color={COLORS.accent}
        />
        <MetricCard
          icon={Activity}
          title="Total Items Sold"
          value={data.totalItems.toLocaleString()}
          subValue={null}
          color={COLORS.purple}
        />
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Sales vs Target */}
        <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">Sales vs Target ðŸŽ¯</h2>
          <ResponsiveContainer width="100%" height={300}>
            <AreaChart data={data.monthlyData}>
              <defs>
                <linearGradient id="salesGradient" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor={COLORS.primary} stopOpacity={0.8}/>
                  <stop offset="95%" stopColor={COLORS.primary} stopOpacity={0.1}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="month" tick={{ fill: '#6B7280' }} />
              <YAxis tick={{ fill: '#6B7280' }} />
              <Tooltip 
                contentStyle={{
                  backgroundColor: 'rgba(255, 255, 255, 0.95)',
                  borderRadius: '6px',
                  border: 'none',
                  boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
                }}
                formatter={(value) => formatCurrency(value)}
              />
              <Legend />
              <Area 
                type="monotone" 
                dataKey="sales" 
                stroke={COLORS.primary} 
                fill="url(#salesGradient)" 
                name="Actual Sales"
              >
                <LabelList content={(props) => <CustomLabel x={props.x} y={props.y} value={props.value} stroke={COLORS.primary} />} />
              </Area>
              <Line 
                type="monotone" 
                dataKey="target" 
                stroke={COLORS.accent} 
                strokeWidth={2}
                name="Target"
                dot={{ r: 4, fill: COLORS.accent }}
              >
                <LabelList content={({ x, y, value }) => <CustomLabel x={x} y={y} value={value} stroke={COLORS.accent} />} />
              </Line>
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* Product Performance */}
        <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">Product Performance ðŸ“ˆ</h2>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={data.productPerformance}
                dataKey="sales"
                nameKey="category"
                cx="50%"
                cy="50%"
                outerRadius={100}
                label={({ name, value }) => `${name}: ${value}%`}
              >
                {data.productPerformance.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={Object.values(COLORS)[index]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Weekly Sales Channels */}
        <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">Sales Channels ðŸ“Š</h2>
          <ResponsiveContainer width="100%" height={300}>
            <ComposedChart data={data.weeklyTrends}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="day" tick={{ fill: '#6B7280' }} />
              <YAxis tick={{ fill: '#6B7280' }} />
              <Tooltip 
                formatter={(value) => formatCurrency(value)}
                contentStyle={{
                  backgroundColor: 'rgba(255, 255, 255, 0.95)',
                  borderRadius: '6px',
                  border: 'none',
                  boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
                }}
              />
              <Legend />
              <Area 
                type="monotone"
                dataKey="inStore" 
                fill={COLORS.primary} 
                stroke={COLORS.primary}
                fillOpacity={0.3}
                name="In-Store"
              >
                <LabelList content={({ x, y, value }) => <CustomLabel x={x} y={y} value={value} stroke={COLORS.primary} />} />
              </Area>
              <Line 
                type="monotone"
                dataKey="takeaway" 
                stroke={COLORS.secondary}
                strokeWidth={2}
                name="Takeaway"
              >
                <LabelList content={({ x, y, value }) => <CustomLabel x={x} y={y} value={value} stroke={COLORS.secondary} />} />
              </Line>
              <Scatter 
                dataKey="delivery" 
                fill={COLORS.accent}
                name="Delivery"
              >
                <LabelList content={({ x, y, value }) => <CustomLabel x={x} y={y} value={value} stroke={COLORS.accent} />} />
              </Scatter>
            </ComposedChart>
          </ResponsiveContainer>
        </div>

        {/* Time Block Analysis */}
        <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">Time Block Analysis ðŸ•’</h2>
          <ResponsiveContainer width="100%" height={300}>
            <ComposedChart data={data.timeBlockData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="name" tick={{ fill: '#6B7280' }} />
              <YAxis yAxisId="left" orientation="left" tick={{ fill: '#6B7280' }} />
              <YAxis yAxisId="right" orientation="right" tick={{ fill: '#6B7280' }} />
              <Tooltip 
                contentStyle={{
                  backgroundColor: 'rgba(255, 255, 255, 0.95)',
                  borderRadius: '6px',
                  border: 'none',
                  boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
                }}
              />
              <Legend />
              <Area
                yAxisId="left"
                dataKey="value"
                fill={COLORS.primary}
                stroke={COLORS.primary}
                fillOpacity={0.3}
                name="Sales"
              >
                <LabelList content={({ x, y, value }) => <CustomLabel x={x} y={y} value={value} stroke={COLORS.primary} />} />
              </Area>
              <Line
                yAxisId="right"
                type="monotone"
                dataKey="customers"
                stroke={COLORS.secondary}
                strokeWidth={2}
                name="Customers"
              >
                <LabelList content={({ x, y, value }) => <CustomLabel x={x} y={y} value={value} stroke={COLORS.secondary} />} />
              </Line>
            </ComposedChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default CafeDashboard;
