import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  PieChart,
  Pie,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
  RadarChart,
  Radar,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  AreaChart,
  Area,
  ComposedChart,
  LabelList,
  Scatter,
  Treemap,
} from "recharts";
import {
  Coffee,
  Users,
  DollarSign,
  ShoppingCart,
  TrendingUp,
  Activity,
} from "lucide-react";

const CafeDashboard = () => {
  const [data, setData] = useState(null);

  // Enhanced vibrant color palette with opacity utilities
  const COLORS = {
    primary: "#4F46E5", // Indigo
    secondary: "#10B981", // Emerald
    accent: "#F59E0B", // Amber
    tertiary: "#EC4899", // Pink
    quaternary: "#8B5CF6", // Purple
    success: "#059669", // Green
    warning: "#DC2626", // Red
    info: "#0EA5E9", // Sky
    neutral: "#6B7280", // Gray
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(value);
  };

  const CustomLabel = ({ x, y, value, stroke }) => (
    <text
      x={x}
      y={y}
      dy={-10}
      fill={stroke}
      fontSize={12}
      textAnchor="middle"
      fontWeight="600"
    >
      {typeof value === "number" ? formatCurrency(value) : value}
    </text>
  );

  const MetricCard = ({ icon: Icon, title, value, subValue, color }) => (
    <div
      className="bg-white p-4 rounded-lg shadow-md border-l-4 hover:shadow-lg transition-shadow duration-200"
      style={{ borderLeftColor: color }}
    >
      <div className="flex items-center justify-between">
        <div>
          <p className="text-gray-500 text-sm font-medium">{title}</p>
          <p className="text-2xl font-bold" style={{ color }}>
            {value}
          </p>
          {subValue && <p className="text-sm text-gray-600">{subValue}</p>}
        </div>
        <div
          className="p-2 rounded-full"
          style={{ backgroundColor: `${color}15` }}
        >
          <Icon size={24} color={color} />
        </div>
      </div>
    </div>
  );

  useEffect(() => {
    const fetchData = async () => {
      setData({
        totalSales: 125000,
        customerCount: 4500,
        avgOrderValue: 27.78,
        totalItems: 8750,
